div.script:
jQuery('document').ready(function(){
var clickEvent = 'ontouchstart' in document.documentElement ? 'touchstart' : 'click';
var child = jQuery('#choose_lang_child');
var title = jQuery('#choose_lang_title');
jQuery('html').bind(clickEvent, function() {
if (jQuery('#choose_lang_child').is(':visible')) {
child.hide();
}
});
jQuery('#choose_lang_title, #choose_lang_child').bind(clickEvent, function(event) {
event.stopPropagation();
});
jQuery('#choose_lang_title').on(clickEvent, function() {
if (child.is(':visible')) {
child.hide();
} else {
child.show();
}
})
return;
})

var searchBoxVisible = false;
var searchBoxTimer = false;
var searchBoxInputFocus = false;
function searchBoxHide() {
searchBoxTimer = setTimeout(function() {
if (searchBoxVisible) {
jQuery('.searchBox .input').fadeOut(function() {
searchBoxVisible = false;
});				
}				
}, 500);			
}
jQuery('#searchBoxForm').submit(function() {
if (jQuery('#searchBox .input .text').val().length == 0) {
return false;
}
});
jQuery('document').ready(function() {
jQuery('#searchBox .text').attr('autocomplete', 'off');
});	
jQuery('.searchBox .text').click(function() {
searchBoxInputFocus = true;
});
jQuery('.searchBox .text').blur(function() {
searchBoxInputFocus = false;
searchBoxHide();
});
jQuery('.searchBox .ico').click(function() {
clearTimeout(searchBoxTimer);
if (!searchBoxVisible) {
searchBoxVisible = true;
searchBoxInputFocus = true;
jQuery('.searchBox .input').fadeIn(function() {
jQuery('#searchBox .text').focus(); 
jQuery('#searchBox .text').click(); 
});				
} else {
searchBoxVisible = false;
searchBoxInputFocus = false;
jQuery('.searchBox .input').fadeOut();
searchBoxHide(); 				
}
});
jQuery('.searchBox').mousemove(function() {
clearTimeout(searchBoxTimer);	
});
jQuery('.searchBox').mouseleave(function() {
if (searchBoxInputFocus) {
return;
}
searchBoxHide();
});

function reloadRates(force) {
if (force || $('#refreshRate').attr('value') == 1) {
$('#currenciesRates').fadeOut('fast').load('/currencies/index'+($('#currency').length?('/'+$('#currency').val()):''), function() {
$('#currenciesRates').fadeIn("slow");
});
}
}
$(document).ready(function() {
var auto_refresh = setInterval(
function(){
reloadRates();
}, 15000);
});
$('#refreshRate').attr('value', 0);
function checkLiveRate() {
if( $('#refreshRate').attr('value')  == 1 ) {
$('#refreshRate').attr('value', 0);
$('#changeLiveRate').removeClass('activeLiveRate');
$('#changeLiveRate').addClass('noActiveLiveRate');
$('#changeLiveRate').html('włącz');
} else {
$('#refreshRate').attr('value', 1)
$('#changeLiveRate').removeClass('noActiveLiveRate');
$('#changeLiveRate').addClass('activeLiveRate');
$('#changeLiveRate').html('wyłącz'); 
}
}
jQuery('#changeLiveRate').click(function(){
checkLiveRate();
})
if (jQuery('#currency').length) {
jQuery('#currency').change(function(){
reloadRates(true);
});
}

